class ContractAddress(rlp.Serializable):
    fields = [
        ("from", rlp.sedes.Binary()),
        ("nonce", rlp.sedes.big_endian_int),
    ]


def test_erc20_contract(w3):
    chain_id = w3.eth.chain_id

    acct = eth_account.Account.from_key(VALIDATOR_PRIV_KEY)

    erc20_contract = w3.eth.contract(abi=abi, bytecode=bytecode["object"])
    tx = erc20_contract.constructor().buildTransaction(
        {
            "chainId": chain_id,
            "gas": 3000000,
            "nonce": w3.eth.get_transaction_count(acct.address, "pending"),
        }
    )
    signed = eth_account.Account.sign_transaction(tx, VALIDATOR_PRIV_KEY)
    txhash = w3.eth.send_raw_transaction(signed.rawTransaction)
    print("deploy contract tx", txhash.hex())
    tx = waittx(w3, txhash)
    print("deploy contract receipt", tx)
    assert tx.status == 1, "tx failed"
    tx_ = w3.eth.get_transaction(txhash)
    print("deploy contract tx", tx_)

    # verify contract address
    print("create address", acct.address, tx_.nonce)
    expect_contract_addr = eth_utils.to_hex(
        eth_utils.keccak(
            rlp.encode(
                ContractAddress(eth_utils.to_bytes(hexstr=acct.address), tx_.nonce)
            )
        )[12:]
    )
    print(
        "contract address, expected:",
        expect_contract_addr,
        "actual:",
        tx.contractAddress,
    )
    assert expect_contract_addr == eth_utils.to_normalized_address(
        tx.contractAddress
    ), "invalid contract address"

    print("get code back", len(w3.eth.get_code(tx.contractAddress)))

    contract = w3.eth.contract(address=tx.contractAddress, abi=abi)

    # check balance
    print(
        "balances",
        acct.address,
        contract.functions.balanceOf(acct.address).call({"from": acct.address}),
        COMMUNITY,
        contract.functions.balanceOf(COMMUNITY).call({"from": acct.address}),
    )

    txhash = contract.functions.transfer(COMMUNITY, 10).transact(
        {"from": acct.address, "chainId": chain_id}
    )
    print("transfer tx", txhash.hex())
    tx = waittx(w3, txhash)
    print("transfer", tx)
    assert tx.status == 1, "tx failed"

    assert len(tx.logs) > 0, "empty logs"

    # check balance
    # FIXME need to set `from` field for ethermint
    print(
        "balances",
        acct.address,
        contract.functions.balanceOf(acct.address).call({"from": acct.address}),
        COMMUNITY,
        contract.functions.balanceOf(COMMUNITY).call({"from": acct.address}),
    )

    print("get block", w3.eth.get_block(tx.blockNumber))

    block = w3.eth.get_block(tx.blockNumber)
    print("transfer block", block)
    assert block.logsBloom == tx.logsBloom, "invalid block logs bloom"

    # offline build transfer
    tx = contract.functions.transfer(COMMUNITY, 10).buildTransaction(
        {
            "chainId": chain_id,
            "gas": 400000,
            "nonce": w3.eth.get_transaction_count(acct.address, "pending"),
        }
    )
    # tx["gas"] = w3.eth.estimateGas(tx)
    signed = eth_account.Account.sign_transaction(tx, VALIDATOR_PRIV_KEY)
    txhash = w3.eth.send_raw_transaction(signed.rawTransaction)
    tx = waittx(w3, txhash)
    print("receipt", tx)
    assert len(tx.logs) > 0, "empty logs"

    # check balance
    print(
        "balances",
        acct.address,
        contract.functions.balanceOf(acct.address).call({"from": acct.address}),
        COMMUNITY,
        contract.functions.balanceOf(COMMUNITY).call({"from": acct.address}),
    )
